import React, { useState, useEffect } from 'react';
import './styles/App.css';
import { carApi, dealerApi } from './services/api';
import CarList from './components/CarList';
import CarForm from './components/CarForm';
import DealerList from './components/DealerList';
import SearchBar from './components/SearchBar';

function App() {
  const [activeTab, setActiveTab] = useState('cars');
  const [cars, setCars] = useState([]);
  const [dealers, setDealers] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [showCarForm, setShowCarForm] = useState(false);
  const [editingCar, setEditingCar] = useState(null);
  const [showDealerForm, setShowDealerForm] = useState(false);
  const [editingDealer, setEditingDealer] = useState(null);

  // Fetch all cars
  const fetchCars = async () => {
    setLoading(true);
    setError('');
    try {
      const response = await carApi.getAll();
      setCars(response.data);
    } catch (err) {
      setError('Failed to load cars. Make sure the backend is running.');
      console.error('Error fetching cars:', err);
    } finally {
      setLoading(false);
    }
  };

  // Fetch all dealers
  const fetchDealers = async () => {
    setLoading(true);
    setError('');
    try {
      const response = await dealerApi.getAll();
      setDealers(response.data);
    } catch (err) {
      setError('Failed to load dealers.');
      console.error('Error fetching dealers:', err);
    } finally {
      setLoading(false);
    }
  };

  // Initial data load
  useEffect(() => {
    fetchCars();
    fetchDealers();
  }, []);

  // Search car by ID
  const searchCarById = async (id) => {
    if (!id) return;

    setLoading(true);
    setError('');
    try {
      const response = await carApi.getById(id);
      setCars([response.data]);
    } catch (err) {
      setError('Car not found. Please check the ID.');
      setCars([]);
    } finally {
      setLoading(false);
    }
  };

  // Search dealer by ID
  const searchDealerById = async (id) => {
    if (!id) return;

    setLoading(true);
    setError('');
    try {
      const response = await dealerApi.getById(id);
      setDealers([response.data]);
    } catch (err) {
      setError('Dealer not found. Please check the ID.');
      setDealers([]);
    } finally {
      setLoading(false);
    }
  };

  // Handle car form submit
  const handleCarSubmit = async (carData) => {
    setLoading(true);
    setError('');
    setSuccess('');

    try {
      if (editingCar) {
        await carApi.update(editingCar.id, carData);
        setSuccess('Car updated successfully!');
      } else {
        await carApi.create(carData);
        setSuccess('Car created successfully!');
      }

      setShowCarForm(false);
      setEditingCar(null);
      fetchCars();
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to save car');
    } finally {
      setLoading(false);
    }
  };

  // Handle dealer form submit
  const handleDealerSubmit = async (dealerData) => {
    setLoading(true);
    setError('');
    setSuccess('');

    try {
      if (editingDealer) {
        await dealerApi.update(editingDealer.id, dealerData);
        setSuccess('Dealer updated successfully!');
      } else {
        await dealerApi.create(dealerData);
        setSuccess('Dealer created successfully!');
      }

      setShowDealerForm(false);
      setEditingDealer(null);
      fetchDealers();
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to save dealer');
    } finally {
      setLoading(false);
    }
  };

  // Delete car
  const deleteCar = async (id) => {
    if (!window.confirm('Are you sure you want to delete this car?')) return;

    setLoading(true);
    setError('');

    try {
      await carApi.delete(id);
      setSuccess('Car deleted successfully!');
      fetchCars();
    } catch (err) {
      setError('Failed to delete car');
    } finally {
      setLoading(false);
    }
  };

  // Delete dealer
  const deleteDealer = async (id) => {
    if (!window.confirm('Are you sure you want to delete this dealer?')) return;

    setLoading(true);
    setError('');

    try {
      await dealerApi.delete(id);
      setSuccess('Dealer deleted successfully!');
      fetchDealers();
    } catch (err) {
      setError('Failed to delete dealer');
    } finally {
      setLoading(false);
    }
  };

  // Edit car
  const editCar = async (id) => {
    const car = cars.find(c => c.id === id);
    if (car) {
      setEditingCar(car);
      setShowCarForm(true);
    }
  };

  // Edit dealer
  const editDealer = async (id) => {
    const dealer = dealers.find(d => d.id === id);
    if (dealer) {
      setEditingDealer(dealer);
      setShowDealerForm(true);
    }
  };

  // Clear messages after 5 seconds
  useEffect(() => {
    const timer = setTimeout(() => {
      setError('');
      setSuccess('');
    }, 5000);
    return () => clearTimeout(timer);
  }, [error, success]);

  return (
    <div className="container">
      <header>
        <h1>üöó Car Dealership Management</h1>
        <p className="subtitle">Full-featured interface for managing cars and dealers</p>
      </header>

      {/* Tabs */}
      <div className="tabs">
        <button
          className={`tab ${activeTab === 'cars' ? 'active' : ''}`}
          onClick={() => setActiveTab('cars')}
        >
          üöó Cars
        </button>
        <button
          className={`tab ${activeTab === 'dealers' ? 'active' : ''}`}
          onClick={() => setActiveTab('dealers')}
        >
          üè¢ Dealers
        </button>
      </div>

      {/* Messages */}
      {error && (
        <div className="message error">
          <span>{error}</span>
          <button onClick={() => setError('')}>‚úï</button>
        </div>
      )}

      {success && (
        <div className="message success">
          <span>{success}</span>
          <button onClick={() => setSuccess('')}>‚úï</button>
        </div>
      )}

      {/* Cars Tab */}
      {activeTab === 'cars' && (
        <>
          <div className="controls">
            <SearchBar
              onSearch={searchCarById}
              onClear={fetchCars}
              placeholder="Enter Car ID..."
            />

            <div className="button-group">
              <button onClick={fetchCars} className="btn btn-success">
                üîÑ Refresh Cars
              </button>
              <button 
                onClick={() => {
                  setEditingCar(null);
                  setShowCarForm(true);
                }} 
                className="btn btn-primary"
              >
                ‚ûï Add New Car
              </button>
            </div>
          </div>

          {loading ? (
            <div className="loading">
              <div className="spinner"></div>
              <p>Loading cars...</p>
            </div>
          ) : (
            <CarList
              cars={cars}
              onEdit={editCar}
              onDelete={deleteCar}
            />
          )}
        </>
      )}

      {/* Dealers Tab */}
      {activeTab === 'dealers' && (
        <>
          <div className="controls">
            <SearchBar
              onSearch={searchDealerById}
              onClear={fetchDealers}
              placeholder="Enter Dealer ID..."
            />

            <div className="button-group">
              <button onClick={fetchDealers} className="btn btn-success">
                üîÑ Refresh Dealers
              </button>
              <button 
                onClick={() => {
                  setEditingDealer(null);
                  setShowDealerForm(true);
                }} 
                className="btn btn-primary"
              >
                ‚ûï Add New Dealer
              </button>
            </div>
          </div>

          {loading ? (
            <div className="loading">
              <div className="spinner"></div>
              <p>Loading dealers...</p>
            </div>
          ) : (
            <DealerList
              dealers={dealers}
              onEdit={editDealer}
              onDelete={deleteDealer}
            />
          )}
        </>
      )}

      {/* Car Form Modal */}
      {showCarForm && (
        <CarForm
          car={editingCar}
          onSubmit={handleCarSubmit}
          onCancel={() => {
            setShowCarForm(false);
            setEditingCar(null);
          }}
        />
      )}

      {/* Dealer Form Modal */}
      {showDealerForm && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h2 className="modal-title">
              {editingDealer ? 'Edit Dealer' : 'Add New Dealer'}
            </h2>

            <form onSubmit={(e) => {
              e.preventDefault();
              const formData = {
                name: e.target.name.value,
                city: e.target.city.value,
                address: e.target.address.value,
                area: e.target.area.value,
                rating: parseFloat(e.target.rating.value),
              };
              handleDealerSubmit(formData);
            }}>
              <div className="form-group">
                <label className="form-label">Name *</label>
                <input
                  type="text"
                  name="name"
                  defaultValue={editingDealer?.name || ''}
                  className="form-input"
                  placeholder="e.g., Best Cars Inc."
                  required
                />
              </div>

              <div className="form-group">
                <label className="form-label">City *</label>
                <input
                  type="text"
                  name="city"
                  defaultValue={editingDealer?.city || ''}
                  className="form-input"
                  placeholder="e.g., Moscow"
                  required
                />
              </div>

              <div className="form-group">
                <label className="form-label">Address *</label>
                <input
                  type="text"
                  name="address"
                  defaultValue={editingDealer?.address || ''}
                  className="form-input"
                  placeholder="e.g., Lenina St. 15"
                  required
                />
              </div>

              <div className="form-group">
                <label className="form-label">Area *</label>
                <input
                  type="text"
                  name="area"
                  defaultValue={editingDealer?.area || ''}
                  className="form-input"
                  placeholder="e.g., Downtown"
                  required
                />
              </div>

              <div className="form-group">
                <label className="form-label">Rating (0-5) *</label>
                <input
                  type="number"
                  name="rating"
                  defaultValue={editingDealer?.rating || ''}
                  className="form-input"
                  min="0"
                  max="5"
                  step="0.1"
                  placeholder="e.g., 4.5"
                  required
                />
              </div>

              <div className="modal-actions">
                <button 
                  type="button" 
                  onClick={() => {
                    setShowDealerForm(false);
                    setEditingDealer(null);
                  }} 
                  className="btn"
                >
                  Cancel
                </button>
                <button type="submit" className="btn btn-primary">
                  {editingDealer ? 'Update Dealer' : 'Add Dealer'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
